import { StyleSheet } from "react-native";
const styles = StyleSheet.create({
    header: {
        padding: 10,
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between"
    },
    txtInput: {
        borderBottomColor: "gray",
        borderBottomWidth: 3,
        width:"40%",
        padding: 2,
        margin: 2
    },
    btn: {
        padding: 2,
        margin: 2,
        width: "20%"
    },
    btntxt:{
        color:"white",
        backgroundColor:"black"
    }
})
export default styles;
