import React from "react";
import Checkbox from "expo-checkbox";
import { FlatList, View, Text } from "react-native";
import styles from "./styles";

const TodoList = ({ todos }) => {

    return (
        <FlatList
            style={{ height: "100%" }}
            data={todos}
            renderItem={({ item }) => {
                return (
                    <View style={styles.item}>
                        <Text> Title: </Text>
                        <Text> {item.title} </Text>
                        <Checkbox value={item.done} />
                    </View>
                );
            }}
            ListHeaderComponent={() => <Text style={styles.listHeader}>My todos {todos.length} of {todos.length}</Text>}
            ListFooterComponent={() => <Text style={styles.listHeader}>End of Todos</Text>}
            ListEmptyComponent={() => <Text>no todos today </Text>}
            ItemSeparatorComponent={() => <View style={styles.seperator}></View>}

        />
    )
};
export default TodoList;
