import { StyleSheet } from "react-native";
const styles = StyleSheet.create({
    header: {
        padding: 10,
        display: "flex",
        flexDirection: "row",
        justifyContent: "space-between"
    },
    txtInput: {
        borderBottomColor: "gray",
        borderBottomWidth: 3,
        width:"40%",
        padding: 2,
        margin: 2
    },
    txtInputFoucused:{
        flexGrow:1,
        backgroundColor:"gray"
    },
    btn: {
        padding: 2,
        margin: 2,
        width: "20%",
        borderRadius:2,
        borderColor:"white"
    },
    btntxt:{
        padding:2,
        color:"white",
        backgroundColor:"black",
        width:"20%",
        fontSize:18
    },
    item:{
        display:flex,
        flexDirection:"row",
        justifyContent:"space-between"
    }
})
export default styles;
