import { StyleSheet } from "react-native";
const styles=StyleSheet.create({
    header:{
        fontSize:23,
        color:"red",
        width:'100%',
        textAlign:"center",
        marginTop:50,
        fontWeight:"bold"
    },
    child:{
        padding:50, 
        alignItems:"center",
        display:"flex",
        flexDirection:"row"
    },
    input:{
        borderBottomColor:"gray",
        borderBottomWidth:2,
        padding:15,
        color:"blue",
        marginRight:15,
        borderColor:"red",
        borderRadius:5
    },
    btn:{
        borderRadius:10,
        padding:3,
        borderColor:"white"
    },
    action:{
        padding:20, 
        alignItems:"center",
        display:"flex",
        flexDirection:"row",
        margin:10,
        alignItems:"center"
    },
    btns:{
        margin:10
    }
});

export default styles;
